const { assert, expect } = require("chai");
const { getNamedAccounts, deployments, ethers, network } = require("hardhat");
const { developmentChains, networkConfig } = require("../../helper-hardhat-config");

const lowSVGImageuri =
    "data:image/svg+xml;base64,";
const highSVGImageUri =
    "data:image/svg+xml;base64,";

const highTokenUri =
    "data:application/json;base64,";
const lowTokenUri =
    "data:application/json;base64,";


!developmentChains.includes(network.name) // if we're not on development chain
    ? describe.skip // SKIP
    : describe("Dynamic SVG NFT Unit Tests", async function () {

        // time to do some tests on a local blockchain
        console.log(`Testing on: ${network.name}`);
        const chainId = network.config.chainId;
    
        let dynamicSvgNft, deployer, lowSVG, highSVG;

        // Before we test, let's grab the contract
        beforeEach(async function () {
            //  Grab contract
            deployer = (await getNamedAccounts()).deployer;
            await deployments.fixture(["mocks", "dynamicsvg"]); // deploys the contracts per tags
            const MockV3AggregatorContract = await deployments.get("MockV3Aggregator"); // gets the contracts
            MockV3Aggregator = await ethers.getContractAt(MockV3AggregatorContract.abi, MockV3AggregatorContract.address);
            const DynamicSvgNftContract = await deployments.get("DynamicSvgNft");
            dynamicSvgNft = await ethers.getContractAt(DynamicSvgNftContract.abi, DynamicSvgNftContract.address);           
        });
        
        describe("constructor", async function () {
            it("00 Initializes the Dynamic VG NFT contract correctly by encoding the ImageURIs", async function () {    
                const tokenCounter = await dynamicSvgNft.getTokenCounter();
                const lowSvg = await dynamicSvgNft.getLowImageUri();
                const highSvg = await dynamicSvgNft.getHighImageUri();
                assert.equal(tokenCounter.toString(), "0");
                // upon contract deployment, the svg files:
                // 1) are abi encoded Packed
                // 2) Base 64 encoded
                // 3) concatenated with the base64 string prefix
                assert.equal(highSvg.toString(),highSVGImageUri);
                assert.equal(lowSvg.toString(), lowSVGImageuri);
            });
        }); // 00 conclude constructor test
    
        describe("MintNft", async function () {
            it("01 It emits the event CreatedNFT and mints the NFT when requested", async function () {
                await new Promise(async (resolve, reject) => {
                    dynamicSvgNft.once("CreatedNFT", async () => {
                        try {
                            const tokenUri_0 = await dynamicSvgNft.tokenURI(0);
                            const tokenCounter = await dynamicSvgNft.getTokenCounter();
                            assert.equal(tokenUri_0,highTokenUri);
                            assert.equal(tokenCounter.toString(), "1");
                            resolve();
                        } catch (error) {
                            console.log(error);
                            reject(error);
                        }
                    }); // This takes place once event has been emitted
                    
                    try { // Before the event...
                        const mintNftResponse= await dynamicSvgNft.mintNft(ethers.utils.parseEther("1000"));
                        const mintNftReceipt = await mintNftResponse.wait(1);
                        console.log("received receipt of NFt mint request");
                    } catch (error) {
                        console.log(error);
                        reject(error);
                    }
                });
            });// Conclude minting with the correct High token URI

            it("02 It emits the event CreatedNFT and mints the Frowning NFT when requested", async function () {
                await new Promise(async (resolve, reject) => {
                    dynamicSvgNft.once("CreatedNFT", async () => {
                        try {
                            const tokenUri_0 = await dynamicSvgNft.tokenURI(0);
                            const tokenCounter = await dynamicSvgNft.getTokenCounter();
                            assert.equal(tokenUri_0,lowTokenUri);
                            assert.equal(tokenCounter.toString(), "1");
                            resolve();
                        } catch (error) {
                            console.log(error);
                            reject(error);
                        }
                    }); // This takes place once event has been emitted
                    
                    try { // Before the event...
                        const mintNftResponse= await dynamicSvgNft.mintNft(ethers.utils.parseEther("1000000000"));
                        const mintNftReceipt = await mintNftResponse.wait(1);
                        console.log("received receipt of NFt mint request");
                    } catch (error) {
                        console.log(error);
                        reject(error);
                    }
                });
            }); // conclude minting of Frowning NFT
        });

    
    }); // Conclude Dynamic SVG NFT tests
        